import React from 'react';
import { StyleSheet, Text, View, FlatList, Dimensions, Image } from 'react-native';

const data = [
  { empty:false, name: 'Pharmacy Faruk', image:"https://lh3.googleusercontent.com/p/AF1QipPYkRw61EBuQXCHzBwb21c4PMt1cOkCkJJNjT7d=w1080-h608-p-no-v0", distance:100, star:2}, 
  { name: 'Pharmacy Emre', image:"https://lh3.googleusercontent.com/p/AF1QipPYkRw61EBuQXCHzBwb21c4PMt1cOkCkJJNjT7d=w1080-h608-p-no-v0", distance:200, star:2}, 
  { name: 'Pharmacy Yunus', image:"https://lh3.googleusercontent.com/p/AF1QipPYkRw61EBuQXCHzBwb21c4PMt1cOkCkJJNjT7d=w1080-h608-p-no-v0", distance:300, star:2}, 
  { name: 'Pharmacy Hilmi', image:"https://lh3.googleusercontent.com/p/AF1QipPYkRw61EBuQXCHzBwb21c4PMt1cOkCkJJNjT7d=w1080-h608-p-no-v0", distance:400, star:2}, 
  { name: 'Pharmacy Robert', image:"https://lh3.googleusercontent.com/p/AF1QipPYkRw61EBuQXCHzBwb21c4PMt1cOkCkJJNjT7d=w1080-h608-p-no-v0", distance:500, star:2}, 
  // { key: 'K' },
  // { key: 'L' },
];

const formatData = (data, numColumns) => {
  const numberOfFullRows = Math.floor(data.length / numColumns);

  let numberOfElementsLastRow = data.length - (numberOfFullRows * numColumns);
  while (numberOfElementsLastRow !== numColumns && numberOfElementsLastRow !== 0) {
    data.push({ key: `blank-${numberOfElementsLastRow}`, empty: true });
    numberOfElementsLastRow++;
  }

  return data;
};

const numColumns = 2;
export default class App extends React.Component {
  renderItem = ({ item, index }) => {
    console.log(item.empty)
    if (item.empty === true) {
      return ;
    }
    return (
      <View
        style={styles.item}
      >
        <Text style={styles.itemText}>{item.name}</Text>
      </View>
    );
  };

  render() {
    return (
      <FlatList
        data={formatData(data, numColumns)}
        style={styles.container}
        renderItem={this.renderItem}
        numColumns={numColumns}
      />
    );
  }
}

const styles = StyleSheet.create({
    imageGrid: {
        width:200,
        height: 200,
        },
    container: {
        width:"80%",
        flex: 1,
        marginVertical: 20,
    },
    item: {
        borderWidth:2,
        borderColor:"#4D243D",
        alignItems: 'center',
        justifyContent: 'center',
        flex: 1,
        marginVertical:10,
        marginHorizontal:15,
        height: Dimensions.get('window').width / numColumns, // approximate a square
    },
    itemInvisible: {
        backgroundColor: 'transparent',
    },
    itemText: {
        color: '#000',
    },
});
