import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, TouchableOpacity, Image, StyleSheet, Alert, ActivityIndicator, Keyboard, TouchableWithoutFeedback, SafeAreaView } from 'react-native';
import Ionicon from "@expo/vector-icons/Ionicons"
import CarouselCards from '../../items/CarouselCards';

export default function PharmacyListScreen({ navigation }) {
    const [prescriptionCode, setPrescriptionCode] = useState('');
    const [isLoading, setIsLoading] = useState(false);
   
    return (
        <View style={styles.container}>
            <TouchableOpacity style={styles.return} onPress={()=>navigation.navigate("Dashboard")}>
                <Ionicon style={{fontSize: 23}} name = "chevron-back-outline"/>
            </TouchableOpacity>

            <Text style={styles.headerText}>PharmNav</Text>
            <Image
                source={require('../../../assets/pharm-nav-icon.png')}
                style={styles.logo}
            />
            {!isLoading && (
                <View style={styles.bottomContainer}>
                    <TextInput
                        style={styles.input}
                        placeholder="Enter Prescription Code"
                        placeholderTextColor="#AFB1B6"
                        onChangeText={(text) => setPrescriptionCode(text)}
                        value={prescriptionCode}
                        maxLength={6}
                        autoCapitalize="characters"
                    />
                    <TouchableOpacity
                        style={styles.findButton}
                    >
                        <Text style={styles.buttonText}>
                            Find Pharmacies
                        </Text>
                    </TouchableOpacity>
                </View>
            )}
            {isLoading && (
                <View style={styles.loadingContainer}>
                    <ActivityIndicator size="large" color="#0000ff" />
                    <Text>Searcing nearest appropriate pharmacies...</Text>
                </View>
            )}

    <SafeAreaView style={styles.container1}>
      <CarouselCards />
    </SafeAreaView>
        </View>
    );
}

const styles = StyleSheet.create({
    container1: {
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        padding: 50
      },
    return:{
        marginTop:10,
        marginLeft:10,
        marginRight:"auto"
    },
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'flex-start',
    },
    bottomContainer: {
        flex: 1,
        alignItems: 'center',
        width: '100%',
    },
    headerText: {
        fontSize: 35,
        marginTop: '10%',
        color:"#ac99d2"
    },
    input: {
        height: 40,
        width: '60%',
        borderColor: 'gray',
        borderWidth: 1,
        marginTop: '7%',
        padding: 10,
        borderRadius:10
    },
    logo: {
        width: 250,
        height: 250,
        borderRadius: 150,
        marginTop: '8%',
    },
    findButton:{
        marginTop:20,
        backgroundColor:"#6f70ff",
        borderRadius:5,
        paddingHorizontal:20,
        paddingVertical:4,
    },
    buttonText:{
        fontSize:17,
        color:"#FFFFFF",
        paddingHorizontal:20,
        paddingVertical:4,
    },
    loadingContainer: {
        position: 'absolute',
        top: '20%',
        left: 0,
        right: 0,
        bottom: 0,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'rgba(255, 255, 255, 0.3)', // Semi-transparent white background
    },
});
